import axios from "axios";
import React, { Component, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { ICategoryName, ITransaction, IRespTransaction } from '../Interfaces'


const ListTransactionMonth = () => {
    const [transactions, setTransactions] = useState<IRespTransaction[]>([])

    useEffect(() => {
        getAllTransactions("january");
    }, [])

    const getAllTransactions = (month: string) => {
        axios.get('http://localhost:8080/api/transactions/' + month).then((response) => {
            setTransactions(response.data)
            console.log(response.data)
        }).catch(error => {
            console.log(error);
        })

    }
    return (
        <div className="container">
            <h2 className="text-center"> List Employees </h2>
            <Link to="/addnewtransaction" className="btn btn-primary mb-2" > Add Transaction </Link>
            <table className="table table-bordered table-striped">
                <thead>
                    <th> Transaction Id </th>
                    <th> Type </th>
                    <th> Category </th>
                    <th> Month </th>
                    <th> Description </th>
                    <th> Amount </th>
                </thead>
                <tbody>
                    {
                        transactions.map(
                            transaction =>
                                <tr key={transaction.id}>
                                    <td> {transaction.id} </td>
                                    <td> {transaction.type} </td>
                                    <td> {transaction.category} </td>
                                    <td>{transaction.month}</td>
                                    <td>{transaction.description}</td>
                                    <td>{transaction.amount}</td>
                                    <td>
                                        {/* <Link className="btn btn-info" to={`/edit-employee/${employee.id}`} >Update</Link>
                                    <button className = "btn btn-danger" onClick = {() => deleteEmployee(employee.id)}
                                    style = {{marginLeft:"10px"}}> Delete</button> */}
                                    </td>
                                </tr>
                        )
                    }
                </tbody>
            </table>
        </div>
    )
}

export default ListTransactionMonth;
